import React from 'react'
import './Checkout.scss'
import ItemRow from './ItemRow'
import Items from './Items'

function Checkout() {



  // if(isEmpty) return (
  //   <div className='checkout'>
  //     <div class = 'checkout__title'>
  //     <h2>Your Basket</h2>
  //     <p>Items you have added to your basket are shown below. Adjust the quantities or remove items before continuing purchase.</p>
  //     </div>
  //     <h1>Basket Is Empty</h1>
  //   </div>
  // )

  return (
    <div class = "checkout">
      <div className='checkout__box'>
      <div class = 'checkout__title'>
      <h2>Your Basket</h2>
      <p>Items you have added to your basket are shown below. Adjust the quantities or remove items before continuing purchase.</p>
     </div>
        <div>
        <table>
           <thead>
            <tr class= 'table__title'>
               <th>Product</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Cost</th>
            </tr>
            </thead>
          <tbody >
        {Items.data.map((item,index) =>{
            return (

               <tr className='table__body' key={index}>
                <td>{item.title} </td>
                <td>Â£{item.price}</td>
                <td className='checkout__quantity'>
                    <button type = 'button' className='checkout__quantity-btn'>-</button>
                    <p className='checkout__quantity-value'>{item.quantity}</p>
                    <button type = 'button' className='checkout__quantity-btn'>+</button>
                </td>
                <td>{item.price*item.quantity}</td>
              </tr>
            )
        })}
          </tbody>
              </table>
        </div>
    </div>
    </div>
  )
}

export default Checkout
