import React, { useState } from 'react'

function Item(name,price,quantity) { 

    const [qty, setQty] = useState(1);

    const decrement = () => setQty(quantity - 1)
    const increment = () => setQty(quantity + 1)
  return (
            <tr className='item'>
                <td>{name}</td>
                <td>£{price}</td>
                <td>
                  <div className='checkout__quantity'>
                  <button type = 'button' onClick = {decrement} className='checkout__quantity-btn'>-</button>
                  <p className='checkout__quantity-value'>{qty}</p>
                  <button type = 'button' onClick={increment} className='checkout__quantity-btn'>+</button>
                  </div>
                </td>
                <td>£{quantity*price}</td>
              </tr>

  )
}

export default Item
