import React, { useState } from 'react'
import './Checkout.scss'
import ItemRow from './ItemRow'
import Items from './Items'

function Checkout() {





  // create array of items and there quntities use the funttionc to update array

  // seperate function componenent for each item 
  return (
    <div class = "checkout">
      <h2>Your Basket</h2>
      <p>Items you have added to your basket are shown below. Adjust the quantities or remove items before continuing purchase.</p>

      <table>
          <thead>
             <tr>
                <th>Product</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Cost</th>
             </tr>
          </thead>
          <tbody>
              <tr>
                {ItemRow.data.map((item, index) => {
                  return (
                    <ItemRow title={item.title}/>
                  )
                })}
              </tr>

          </tbody>
      </table>
    </div>
  )
}

export default Checkout
