import React, { useState } from 'react'

function ItemRow(props) {
  const [qty, setQty]= useState(0);

  const decrement = ()=> {
    setQty(qty - 1)
  }

  const increment = ()=> {
    setQty(qty + 1)
  }

  return (
    <div className='itemRow'>
             <td>{props.title}</td>
                <td>Â£{props.price}</td>
                <td>
                  <div className='checkout__quantity'>
                    <button onClick ={decrement} type = 'button' className='checkout__quantity-btn'>-</button>
                    <p className='checkout__quantity-value'>{props.quantity}</p>
                    <button onClick ={increment} type = 'button' className='checkout__quantity-btn'>+</button>
                  </div>
                </td>
            <td>{props.price*props.quantity}</td>
    </div>
  )
}

export default ItemRow
